		PublishingHouse publishingHouse = (PublishingHouse) target;
		if (publishingHouse.getName().trim().length() == 0)
			errors.rejectValue("name", "", "Tên nhà xuất bản không được rỗng");

		if (publishingHouse.getName().length() < 3 || publishingHouse.getName().length() > 50)
			errors.rejectValue("name", "", "Tên nhà xuất bản phải có độ dài lớn hơn 3 và nhỏ hơn 50 ký tự");
		
